import React, { useEffect, useState } from 'react'
import Logo from "../Logo/Logo"
import Nav from "../Nav/Nav";
import CTA from "../CTA/CTA";

const StickyHeader = () => {
  let [isSticky, setSticky] = useState(false);
  let ref = 0;

  const handleScroll = () => {
    ref = window.pageYOffset
    setSticky(ref >= 496)
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', () => handleScroll);
    };
  }, [])

  return (
    <div id={isSticky ? 'sticky-header' : ''} className="sticky-header-wrapper">
      <Breakpoint large up className="nav-menu">
        {content.main.map(link => {
          return <div className="nav-menu-item" key={link.id} className={"https://melvinvmegen.github.io/Portfolio/#" + link.name.replace(/ /g, "-") === document.URL ? "nav-anim" : ""} >
            <a href={"#" + link.name.replace(/ /g, "-")}>
              <span className="text">{link.name}</span>
              <span className="line -bottom"></span>
            </a>
          </div>
        })}
      </Breakpoint>
      <Breakpoint large up className="sticky-header-container">
        <Logo />
        <Nav />
        <CTA />
      </Breakpoint>
      <div className="sticky-header-container">
        <Logo />
        <Nav />
        <CTA />
      </div>
    </div>
  )
}

export default StickyHeader;
