import React, { Fragment } from 'react'
import Modal from "../../Components/Modal/Modal";

const withErrorHandler = (WrappedComponent) => {
  return (props) => {
    return (
      <Fragment>
        <WrappedComponent {...props} />
        <Modal show>
          Something didn't work!
        </Modal>
      </Fragment>
    )
  }
}

export default withErrorHandler;
