import React from "react";
import Logo from "./Logo/Logo";
import Nav from "./Nav/Nav";
import CTA from "./CTA/CTA";
import StickyHeader from "./StickyHeader/StickyHeader";
import MobileMenu from "./MobileMenu/MobileMenu"
import { Component } from "react";

class Header extends Component {
  state = {
    action: "Recrutez-moi !",
    show: false,
    isSticky: false,
    ref: null
  }

  toggleMenu = () => {
    const doesShow = this.state.show;
    this.setState({ show: !doesShow })
  }

  handleScroll = () => {
    this.setState({ ref: window.pageYOffset })
    console.log(this.state.ref)
    if ( this.state.ref >= 500) {
      this.setState({
        isSticky: true
      });
    }
    else {
      this.setState({
        isSticky: false
      });
    }
  };

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);

    return () => {
      window.removeEventListener('scroll', () => this.handleScroll);
    };
  }


  render() {
    return (
      <div className="site-header" id={this.state.isSticky ? 'sticky-header' : ''} ref={this.state.ref}>
        <div className="site-header-wrapper">
          <header className="main-header d-none d-sm-none d-md-none d-lg-flex">
            <Logo />
            <Nav />
            <CTA action={this.state.action} />
          </header>
          <MobileMenu show={this.state.show} toggle={this.toggleMenu} />
        </div>
        {/* <StickyHeader /> */}
      </div>
    )
  }
}

export default Header;
