import React, { useEffect, useState } from 'react'
import Logo from "../Logo/Logo"
import Nav from "../Nav/Nav";
import CTA from "../CTA/CTA";
import { Breakpoint } from 'react-socks';
import MobileMenu from '../MobileMenu/MobileMenu'

const StickyHeader = (props) => {
  let [isSticky, setSticky] = useState(false);
  let ref = 0;

  const handleScroll = () => {
    ref = window.pageYOffset
    const sectionTwo = document.getElementById("section-two");
    const sectionThree = document.getElementById("section-three");
    const sectionGallery = document.querySelector(".gallery-full");
    const sectionTestimonials = document.querySelector(".section-testimonial")
    const sectionFooter = document.querySelector(".component-footer")
    console.log(sectionTwo.offsetTop);
    console.log(sectionThree.offsetTop);
    console.log(sectionGallery.offsetTop);
    console.log(sectionTestimonials.offsetTop);
    console.log(sectionFooter.offsetTop);
    setSticky(ref >= 496 && ref <= sectionTwo.offsetTop - 5
      || ref >= sectionTwo && ref <= sectionThree.offsetTop - 5
      || ref >= sectionThree && ref <= sectionGallery.offsetTop - 5
      || ref >= sectionGallery && ref <= sectionTestimonials.offsetTop - 5
      || ref >= sectionFooter)
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', () => handleScroll);
    };
  }, [])

  return (
    <div id={isSticky ? 'sticky-header' : ''} className="sticky-header-wrapper">
      <Breakpoint large up className="sticky-header-container">
        <Logo />
        <Nav />
        <CTA modal={props.modal} />
      </Breakpoint>
      <Breakpoint medium down className="sticky-header-container">
        <MobileMenu show={props.show} toggle={props.toggle} />
      </Breakpoint>
    </div>
  )
}

export default StickyHeader;
