import React, { Component } from 'react'

class Newsletter extends Component {
  state = {
    loading: false,
    orderForm: {
      name: {
        elementType: 'input',
        elementConfig: {
          type: 'text',
          placeholder: 'Nom, prénom'
        },
        value: '',
        validation: {
          required: true,
        },
        valid: false,
        touched: false
      },
      company: {
        elementType: 'input',
        elementConfig: {
          type: 'text',
          placeholder: 'Entreprise'
        },
        value: '',
        validation: {
          required: true,
        },
        valid: false,
        touched: false
      },
      email: {
        elementType: 'input',
        elementConfig: {
          type: 'email',
          placeholder: 'E mail@gmail.com'
        },
        value: '',
        validation: {
          required: true,
          isEmail: true
        },
        valid: false,
        touched: false
      },
      city: {
        elementType: 'input',
        elementConfig: {
          type: 'text',
          placeholder: 'Ville'
        },
        value: '',
        validation: {
          required: true,
        },
        valid: false,
        touched: false
      },
      request: {
        elementType: 'input',
        elementConfig: {
          type: 'text',
          placeholder: 'Demande'
        },
        value: '',
        validation: {
          required: true,
        },
        valid: false,
        touched: false
      },
    },
    formIsValid: false
  }

  render() {
    return (
      <div className="footer-newsletter d-none d-sm-block">
        <p className="menu-item-newsletter"><span>Rejoingnez ma newsletter</span></p>
        <form className="newsletter-form">
          <input className="newsletter-input" type="text" name="Email" placeholder="name@website.com"
            aria-required="true" />
          <button type="submit" value="subscribe" name="subscribe" className="button-newsletter">S'inscrire</button>
        </form>
      </div>
    )
  }
}

export default Newsletter;
