import React, { useContext, useRef } from 'react'
import ContentContext from '../../../context/content-context'
import { Breakpoint } from 'react-socks';

const Nav = () => {
  const menu = useRef();
  const content = useContext(ContentContext)
  const anchor = "http://localhost:3000/#Developpeur-Front-end"

  return (
    <div className="main-header-center">
      <Breakpoint large up className="nav-menu">
        {content.main.map(link => {
          return <div className="nav-menu-item-first" key={link.id}>
            <a ref={menu} href={"#" + link.name.replace(/ /g, "-")} className={anchor === document.URL ? "active" : ""}><span className="text">{link.name}</span></a>
            <span className="line -bottom"></span>
            {console.log(menu.current)}
          </div>
        })}
      </Breakpoint>
      <Breakpoint medium down className="nav-menu-mobile">
        {content.main.map(link => {
          return <div className="nav-menu-item" key={link.id}>
            <a href={"#" + link.name.replace(/ /g, "-")} className=""><span className="text">{link.name}</span></a>
            <span className="line -bottom"></span>
          </div>
        })}
      </Breakpoint>
    </div>
  )
}

export default Nav;
