import React, { Component } from 'react'
import Button from '../FormSubmit/FormSubmit'
import Input from "../Input/Input"
import axios from '../../axios-contacts'
import classes from './ContactForm.module.css'
import Spinner from "../Spinner/Spinner";


class Newsletter extends Component {
  state = {
    loading: false,
    orderForm: {
      email: {
        elementType: 'input',
        elementConfig: {
          type: 'email',
          placeholder: 'E mail@gmail.com'
        },
        value: '',
        validation: {
          required: true,
          isEmail: true
        },
        valid: false,
        touched: false
      }
    },
    formIsValid: false
  }

  inputChangedHandler = (event, inputIdentifier) => {
    const form = {
      ...this.state.orderForm
    };
    const updatedFormElement = {
      ...form[inputIdentifier]
    };
    updatedFormElement.value = event.target.value;
    updatedFormElement.valid = this.checkValidity(updatedFormElement.value, updatedFormElement.validation);
    updatedFormElement.touched = true;
    form[inputIdentifier] = updatedFormElement
    let formIsValid = true;
    for (let inputIdentifier in form) {
      formIsValid = form[inputIdentifier].valid && formIsValid;

    }
    this.setState({ orderForm: form, formIsValid: formIsValid })
  }

  contactHandler = (event) => {
    event.preventDefault();
    this.setState({ loading: true });
    const formData = {};
    for (let formElement in this.state.orderForm) {
      formData[formElement] = this.state.orderForm[formElement].value;
    }
    const contact = {
      contactData: formData
    }
    axios.post('/contacts.json', contact)
      .then(response => {
        this.setState({ loading: false }, () => {
          this.props.modalClosed();
        })
      })
      .catch(error => {
        this.setState({ loading: false }, () => {
          this.props.modalClosed();
        })
      });
  }

  render() {
    return (
      <div className="footer-newsletter d-none d-sm-block">
        <p className="menu-item-newsletter"><span>Rejoingnez ma newsletter</span></p>
        <form className="newsletter-form">
          <input className="newsletter-input" type="text" name="Email" placeholder="name@website.com"
            aria-required="true" />
          <button type="submit" value="subscribe" name="subscribe" className="button-newsletter">S'inscrire</button>
        </form>
      </div>
    )
  }
}

export default Newsletter;
